import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ProductService } from '../services/product.service';

@Component({
  selector: 'app-create-product',
  templateUrl: './create-product.component.html',
  styleUrls: ['./create-product.component.scss']
})
export class CreateProductComponent implements OnInit{
  ngOnInit(): void {

  }

  constructor(private productService: ProductService) {

  }

   form = new FormGroup( {
     title: new FormControl<string>('', [
      Validators.required,
      Validators.minLength(3)

     ])
  })

  get title() {
    return this.form.controls.title as FormControl
  }


  submit() {
    console.log(this.form.value)
    this.productService.create({ 
        title: this.form.value as string,
        price: 13.5,
        description: 'Description',
        image: 'https://i.pravatar.cc',
        category: 'electronic', 
        rating: {
          rate: 1,
          count: 2
        }
      }

    )
  }

}
<form [formGroup]="form" (ngSubmit)="submit()">
    <div class="flex flex-col items-center">
        <input type="text" class="border rounded w-full py-2 px-4 mb-2 "
        placeholder="Product Name" formControlName="title" appFocus>
        <p class="text-red-600 font-medium mb-2" *ngIf="title.errors?.minlength && title.touched">Minimum 1 character</p>
        <p class="text-red-600 font-medium mb-2" *ngIf="title.errors?.required && title.touched">Property required</p>
        <button class=" py-2 px-4 bg-blue-600 text-white" type="submit">Create</button>
    </div>
</form>
