import { Component, Input, OnInit } from '@angular/core';
import { ModalService } from 'src/app/services/modal.service';

@Component({
  selector: 'app-modal',
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.scss']
})
export class ModalComponent implements OnInit {

  @Input() title: string

  constructor(public modalService: ModalService) {}


  ngOnInit(): void {

  }

}
<div class="bg-black/50 fixed top-0 right-0 left-0 bottom-0" 
(click)="modalService.close()"> </div>

<div class="w-[500px] absolute top-16 rounded p-6 bg-white left-1/2 -translate-x-1/2">

    <h1 class="text-center mb-2 text-lg">{{title | titlecase}}</h1>
    <ng-content></ng-content>

</div>
